import unittest

from pngme.chunk import Chunk
from pngme.chunk_type import ChunkType
from pngme.png import Png


class TestPng(unittest.TestCase):
    def _testing_chunks(self) -> list[Chunk]:
        chunks = []
        chunks.append(self._chunk_from_strings("FrSt", "I am the first chunk"))
        chunks.append(self._chunk_from_strings("miDl", "I am another chunk"))
        chunks.append(self._chunk_from_strings("LASt", "I am the last chunk"))
        return chunks

    def _testing_png(self) -> Png:
        return Png.from_chunks(self._testing_chunks())

    def _chunk_from_strings(self, chunk_type: str, data: str) -> Chunk:
        return Chunk.new(ChunkType.from_str(chunk_type), data.encode())

    def test_from_chunks(self) -> None:
        chunks = self._testing_chunks()
        png = Png.from_chunks(chunks)
        self.assertEqual(len(png.chunks()), 3)

    def test_valid_from_bytes(self) -> None:
        chunk_bytes: bytes = b"".join(
            chunk.as_bytes() for chunk in self._testing_chunks()
        )
        bytes_ = Png.STANDARD_HEADER + chunk_bytes
        png = Png.from_bytes(bytes_)

    def test_invalid_header(self) -> None:
        chunk_bytes: bytes = b"".join(
            chunk.as_bytes() for chunk in self._testing_chunks()
        )
        bytes_ = b"\x0dPNG\r\n\x1a\n" + chunk_bytes
        with self.assertRaises(ValueError):
            Png.from_bytes(bytes_)

    def test_invalid_chunk(self) -> None:
        chunk_bytes: bytes = b"".join(
            chunk.as_bytes() for chunk in self._testing_chunks()
        )
        bad_chunk = b"\x00\x00\x00\x05 uStA@ABC\x01\x02\x03\x04\x05"
        bytes_ = Png.STANDARD_HEADER + chunk_bytes + bad_chunk
        with self.assertRaises(ValueError):
            Png.from_bytes(bytes_)

    def test_list_chunks(self) -> None:
        png = self._testing_png()
        chunks = png.chunks()
        self.assertEqual(len(chunks), 3)

    def test_chunk_by_type(self) -> None:
        png = self._testing_png()
        chunk = png.chunks_by_type(ChunkType.from_str("FrSt"))[0]
        self.assertEqual(str(chunk.chunk_type), "FrSt")
        self.assertEqual(chunk.data().decode(), "I am the first chunk")

    def test_append_chunk(self) -> None:
        png = self._testing_png()
        png.append_chunk(self._chunk_from_strings("TeSt", "Message"))
        chunk = png.chunks_by_type(ChunkType.from_str("TeSt"))[0]
        self.assertEqual(str(chunk.chunk_type), "TeSt")
        self.assertEqual(chunk.data().decode(), "Message")

    def test_remove_chunk(self) -> None:
        png = self._testing_png()
        png.append_chunk(self._chunk_from_strings("TeSt", "Message"))
        png.remove_chunk(ChunkType.from_str("TeSt"))
        chunks = png.chunks_by_type(ChunkType.from_str("TeSt"))
        self.assertEqual(len(chunks), 0)

    def test_png_from_image_file(self) -> None:
        png = Png.from_bytes(self.PNG_FILE)

    def test_as_bytes(self) -> None:
        png = Png.from_bytes(self.PNG_FILE)
        actual = png.as_bytes()
        self.assertEqual(actual, self.PNG_FILE)

    def test_png_trait_impls(self) -> None:
        chunk_bytes: bytes = b"".join(
            chunk.as_bytes() for chunk in self._testing_chunks()
        )
        bytes_ = Png.STANDARD_HEADER + chunk_bytes
        png = Png.from_bytes(bytes_)
        _png_string = str(png)

